</span></span><span style="display: flex;"><span>    <span style="color: #60a0b0; font-style: italic;"># Uses tmpfs by default (ephemeral), which is fine since we</span>
</span></span><span style="display: flex;"><span>    <span style="color: #60a0b0; font-style: italic;"># don't build anything in the VM.</span>
</span></span><span style="display: flex;"><span>    writableStoreOverlay <span style="color: #666;">=</span> <span style="color: #4070a0;">"/nix/.rw-store"</span>;
</span></span><span style="display: flex;"><span>
</span></span><span style="display: flex;"><span>    volumes <span style="color: #666;">=</span> [
</span></span><span style="display: flex;"><span>      {
</span></span><span style="display: flex;"><span>        mountPoint <span style="color: #666;">=</span> <span style="color: #4070a0;">"/var"</span>;
</span></span><span style="display: flex;"><span>        image <span style="color: #666;">=</span> <span style="color: #4070a0;">"var.img"</span>;
</span></span><span style="display: flex;"><span>        size <span style="color: #666;">=</span> <span style="color: #40a070;">8192</span>; <span style="color: #60a0b0; font-style: italic;"># MB</span>
