</span></span><span style="display: flex;"><span>        source <span style="color: #666;">=</span> <span style="color: #4070a0;">"/nix/store"</span>;
</span></span><span style="display: flex;"><span>        mountPoint <span style="color: #666;">=</span> <span style="color: #4070a0;">"/nix/.ro-store"</span>;
</span></span><span style="display: flex;"><span>      }
</span></span><span style="display: flex;"><span>      {
</span></span><span style="display: flex;"><span>        proto <span style="color: #666;">=</span> <span style="color: #4070a0;">"virtiofs"</span>;
</span></span><span style="display: flex;"><span>        tag <span style="color: #666;">=</span> <span style="color: #4070a0;">"ssh-keys"</span>;
</span></span><span style="display: flex;"><span>        source <span style="color: #666;">=</span> <span style="color: #4070a0;">"</span><span style="color: #70a0d0;">${</span>workspace<span style="color: #70a0d0;">}</span><span style="color: #4070a0;">/ssh-host-keys"</span>;
</span></span><span style="display: flex;"><span>        mountPoint <span style="color: #666;">=</span> <span style="color: #4070a0;">"/etc/ssh/host-keys"</span>;
</span></span><span style="display: flex;"><span>      }
</span></span><span style="display: flex;"><span>      {
</span></span><span style="display: flex;"><span>        proto <span style="color: #666;">=</span> <span style="color: #4070a0;">"virtiofs"</span>;
</span></span><span style="display: flex;"><span>        tag <span style="color: #666;">=</span> <span style="color: #4070a0;">"claude-credentials"</span>;
</span></span><span style="display: flex;"><span>        source <span style="color: #666;">=</span> <span style="color: #4070a0;">"/home/michael/claude-microvm"</span>;
